    let decodePath = this.request.doNotEncodePath;
    let decodeSlashesInPath = false;
    let firstValOnly = false;
    let bodyHash = hash(this.request.body || '', 'hex');

    if (query) {
      let reducedQuery = Object.keys(query).reduce(function (obj, key) {
        if (!key) {
