    if (query) {
      let reducedQuery = Object.keys(query).reduce(function (obj, key) {
        if (!key) {
          return obj;
        }
        obj[encodeRfc3986Full(key)] = !Array.isArray(query[key]) ? query[key] :
          (firstValOnly ? query[key][0] : query[key]);
        return obj;
      }, {});
      let encodedQueryPieces = [];
