  'connection': true,
  'x-amzn-trace-id': true,
  'user-agent': true,
  'expect': true,
  'presigned-expires': true,
  'range': true,
};

const HEADERS_TO_INCLUDE = ["x\-fp\-.*", "host"];

// request: { path | body, [host], [method], [headers], [service], [region] }
class RequestSigner {
