const url = require('url');
const querystring = require('querystring');
const crypto = require('crypto-js');
const sha256 = crypto.SHA256;
const hmacSHA256 =  crypto.HmacSHA256;

function hmac(key, string, encoding) {
  return hmacSHA256(string, key).toString();
}

function hash(string, encoding) {
  return sha256(string).toString();
}

// This function assumes the string has already been percent encoded
function encodeRfc3986(urlEncodedString) {
  return urlEncodedString.replace(/[!'()*]/g, function (c) {
    return '%' + c.charCodeAt(0).toString(16).toUpperCase()
  });
}

function encodeRfc3986Full(str) {
  return str;
  // return encodeRfc3986(encodeURIComponent(str));
}

const HEADERS_TO_IGNORE = {
  'authorization': true,
  'connection': true,
  'x-amzn-trace-id': true,
  'user-agent': true,
  'expect': true,
  'presigned-expires': true,
  'range': true,
};

const HEADERS_TO_INCLUDE = ["x\-fp\-.*", "host"];

// request: { path | body, [host], [method], [headers], [service], [region] }
class RequestSigner {
  constructor(request) {
    if (typeof request === 'string') {
      request = url.parse(request);
    }

    let headers = request.headers = (request.headers || {});
    request.headers['host'] = pm.collectionVariables.get("baseUrl").replace("https://", "");
    this.request = request;

