  sign() {
    if (!this.parsedPath) {
      this.prepareRequest();
    }
    if (this.request.signQuery) {
      this.parsedPath.query['x-fp-signature'] = this.signature();
    } else {
      this.request.headers['x-fp-signature'] = this.signature();
    }

    this.request.path = this.formatPath();
    return this.request;
