        }

        if (request.body && !headers['Content-Length'] && !headers['content-length']) {
          headers['Content-Length'] = Buffer.byteLength(request.body);
        }

        if (headers['x-fp-date']) {
          this.datetime = headers['x-fp-date'] || headers['x-fp-date'];
        } else {
