      let date = new Date(headers.Date || headers.date || new Date());

      this.datetime = date.toISOString().replace(/[:\-]|\.\d{3}/g, '');
    }
    return this.datetime;
  }

  getDate() {
    return this.getDateTime().substr(0, 8);
