        if (normalizePath && piece === '..') {
          path.pop();
        } else if (!normalizePath || piece !== '.') {
          if (decodePath) piece = decodeURIComponent(piece.replace(/\+/g, ' '))
          path.push(encodeRfc3986Full(piece));
        }
        return path;
      }, []).join('/');
      if (pathStr[0] !== '/') pathStr = '/' + pathStr;
      if (decodeSlashesInPath) pathStr = pathStr.replace(/%2F/g, '/');
    }

    let canonicalReq = [
      this.request.method || 'GET',
      pathStr,
      queryStr,
      this.canonicalHeaders() + '\n',
      this.signedHeaders(),
      bodyHash,
    ].join('\n');
    return canonicalReq;
  }

  canonicalHeaders() {
    let headers = this.request.headers;

    function trimAll(header) {
      return header.toString().trim().replace(/\s+/g, ' ');
    }
    return Object.keys(headers)
      .filter(function (key) {
        let notInIgnoreHeader = HEADERS_TO_IGNORE[key.toLowerCase()] == null;
        if (notInIgnoreHeader) {
          let foundMatch = false;
          for (let t in HEADERS_TO_INCLUDE) {
            foundMatch = foundMatch || new RegExp(HEADERS_TO_INCLUDE[t], "ig").test(key);
          }
          return foundMatch;
        } else {
          return false;
        }
      })
      .sort(function (a, b) {
        return a.toLowerCase() < b.toLowerCase() ? -1 : 1;
      })
      .map(function (key) {
        return key.toLowerCase() + ':' + trimAll(headers[key]);
      })
      .join('\n');
  }

  signedHeaders() {
    return Object.keys(this.request.headers)
      .map(function (key) {
        return key.toLowerCase();
      })
      .filter(function (key) {

        let notInIgnoreHeader = HEADERS_TO_IGNORE[key.toLowerCase()] == null;
        if (notInIgnoreHeader) {
          let foundMatch = false;
          for (let t in HEADERS_TO_INCLUDE) {
            foundMatch = foundMatch || new RegExp(HEADERS_TO_INCLUDE[t], "ig").test(key);
          }
          return foundMatch;
        } else {
          return false;
        }
      })
      .sort()
      .join(';');
  }

  parsePath() {
    let path = this.request.path || '/';
    let queryIx = path.indexOf('?');
    let query = null;

    if (queryIx >= 0) {
      query = querystring.parse(path.slice(queryIx + 1));
      path = path.slice(0, queryIx);
    }
    path = path.split("/").map((t) => {
      return encodeURIComponent(decodeURIComponent(t));
    }).join("/");

    this.parsedPath = {
      path: path,
      query: query,
    };
  }

  formatPath() {
    let path = this.parsedPath.path;
    let query = this.parsedPath.query;

    if (!query) {
      return path;
    }

    // Services don't support empty query string keys
    if (query[''] != null) {
      delete query[''];
    }

    return path + '?' + encodeRfc3986(querystring.stringify(query));
  }
}


let signingOptions = {
    method: pm.request.method.toUpperCase(),
    host: pm.collectionVariables.get("baseUrl").replace("https://", ""),
    path: pm.request.url.getPathWithQuery(),
    body: pm.request.body ? pm.request.body.toString() : null,
    headers: pm.request.headers
}

