};

const HEADERS_TO_INCLUDE = ["x\-fp\-.*", "host"];

// request: { path | body, [host], [method], [headers], [service], [region] }
class RequestSigner {
  constructor(request) {
    if (typeof request === 'string') {
      request = url.parse(request);
    }

    let headers = request.headers = (request.headers || {});
