const sha256 = crypto.SHA256;
const hmacSHA256 =  crypto.HmacSHA256;

function hmac(key, string, encoding) {
  return hmacSHA256(string, key).toString();
}

function hash(string, encoding) {
  return sha256(string).toString();
}

